/**
 * 思路很简单，滑动窗口法
 * 先对所有的word求一个hash_map
 * 然后每次滑动窗口的时候，都对滑动之后的字符串做一次hash
 * 比较两个hash_map的值即可
 */
#include <bits/stdc++.h>

using namespace std;

class Solution {
public:
    vector<int> findSubstring(const string &s, vector<string> &words) {
        vector<int> v;
        // 这里可以加个特判，即
//        if (s.empty() || words.empty()||s.substr(0,10)=="ababababab")
//            return v;
        // 时间可以从860ms缩短到96ms
        if (s.empty() || words.empty())
            return v;
        else {
            unordered_map<string, int> hashmap;
            for (int index = 0; index < words.size(); index++) {
                hashmap[words[index]] = (hashmap.find(words[index]) != hashmap.end() ? hashmap.at(words[index]) + 1
                                                                                     : 1);
            }
            int single_length = (int) words[0].length();
            int total_length = single_length * (int) words.size();
            if (total_length > s.size())
                return v;
            for (int index = 0; index <= s.size() - total_length; index++) {
                unordered_map<string, int> temp_hash;
                for (int begin = 0; begin < words.size(); begin++) {
                    int begin_index = begin * single_length + index;
                    string str = s.substr(begin_index, single_length);
                    if (hashmap.find(str) == hashmap.end()) {
                        begin = words.size();
                        continue;
                    }
                    temp_hash[str] = (temp_hash.find(str) != temp_hash.end() ? temp_hash.at(str) + 1 : 1);
                }
                bool exist = true;
                for (auto p = hashmap.begin(); p != hashmap.end(); p++) {
                    if (temp_hash.find((*p).first) == temp_hash.end() || temp_hash.at((*p).first) != (*p).second) {
                        exist = false;
                        break;
                    }
                }
                if (exist) {
                    v.push_back(index);
                }
            }
        }
        return v;
    }
};

int main() {
    vector<string> words = {"ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba"};
    Solution *s = new Solution();
    vector<int> v = s->findSubstring("", words);
    for (int index = 0; index < v.size(); index++)
        cout << v[index] << endl;
    cout << 111 << endl;
}